App::uses('Object', 'Core');
App::uses('File', 'Utility');
App::uses('Folder', 'Utility');

require_once dirname(dirname(__FILE__)) . DS . 'Case' . DS . 'constants.php';

/**
 * Test Data Class
 *
 * Holds and manages test data used in cases.
 *
 * @package       Media.Test.Fixture
 */
class TestData extends Object {

/**
 * Base64 encoded data
 *
 * Sample data can be generated easily on *nix systems
 * from the command line with one of the several available base64 tools
 * and the `head` command with the `-c` option.
 *
 * @var array
 */
	public $encoded = array(
		'application-pdf.pdf' => '',
		'audio-mp3.mp3'       => '',
		'image-jpg.jpg'       => '',
		'image-png.png'       => '',
		'image-gif.gif'       => '',
		'video-mov.mov'       => '',
		'video-mpg.mpg'       => '',
		'video-rm.rm'         => '',
		'video-swf.swf'       => 'Q1dTCCQAAAB42otgqGBgfMDAwMPI4CLIwMDQ4MDmzPT//38HIBsATxAGXA==',
		'video-wmv.wmv'       => '',
	);

/**
 * Buffers decoded file data.
 *
 * @var array
 */
	public $decoded = array();

/**
 * An array of File objects retrieved.
 *
 * @var array
 */
	public $Files = array();

/**
 * Directory below which files are being created.
 *
 * @var string
 */
	public $settings = array();

/**
 * Constructor
 *
 * @param array $settings
 */
	public function __construct(array $settings = array()) {
		$default = array(
			'base' => MEDIA
		);
		$settings += $default;

		$base = $settings['base'];
		$this->settings = $settings + array(
			'static'   => $base . 'static' . DS,
			'filter'   => $base . 'filter' . DS,
			'transfer' => $base . 'transfer' . DS
		);

		extract($this->settings);
		/* @var $base string */
		/* @var $static string */
		/* @var $filter string */
		/* @var $transfer string */

		$this->Folder = new Folder($base, true);
		$this->Folder->create($static . 'aud');
		$this->Folder->create($static . 'img');
		$this->Folder->create($static . 'doc');
		$this->Folder->create($static . 'txt');
		$this->Folder->create($static . 'vid');
		$this->Folder->create($filter);
		$this->Folder->create($transfer . 'img');
		$this->Folder->create($transfer . 'doc');
		$this->Folder->create($transfer . 'txt');
	}

/**
 * Destructor
 *
 */
	public function __destruct() {
		$this->cleanUp();
	}

/**
 * Returns the test data for a given key.
 *
 * @param string $key
 * @return string
 */
	public function getString($key) {
		$result = '';

		if (isset($this->decoded[$key])) {
			$result = $this->decoded[$key];
		} elseif (isset($this->encoded[$key])) {
			$this->decoded[$key] = base64_decode($this->encoded[$key]);

			if (preg_match('/^[ais]:[0-9]+/', $this->decoded[$key])) {
				$this->decoded[$key] = unserialize($this->decoded[$key]);
			}

			$result = $this->decoded[$key];
		}

		return $result;
	}

/**
 * Creates a file with contents from base64 encoded string.
 *
 * TestData::getFile('image-png.png');
 * TestData::getFile(array('image-png.png' => TMP . 'other-name.png'));
 * TestData::getFile(array('image-png.png' => 'other-name.png'));
 * TestData::getFile('file.txt', 'I am the content');
 *
 * @param mixed $key
 * @param string $string
 * @return string Absolute path to the created file
 */
	public function getFile($key = null, $string = '') {
		if (is_array($key)) {
			$file = current($key);
			$key = key($key);

			if (!Folder::isAbsolute($file)) {
				$file = $this->settings['base'] . $file;
			}
			$alias = $file;
		} else {
			$alias = $key;
			$file = $this->settings['base'] . $key;
		}

		if ($string === '') {
			$string = $this->getString($key);
		}

		$File = new File($file);

		if ($File->exists()) {
			$File->delete();
		}

		$File->write($string);
		$File->offset(0);
		$File->close();

		$this->Files[$alias] =& $File;
		return $File->pwd();
	}

/**
 * Deletes all files which have been retrieved through TestData::getFile,
 * as well as all folders that were created.
 *
 * Most often called from the tearDown method of a test case.
 *
 * @return void
 */
	public function cleanUp() {
		foreach ($this->Files as $File) { /* @var $File File */
			if ($File->exists()) {
				$File->delete();
			}
		}
		$this->Files = array();

		$this->Folder->delete();
	}

}
